$:/_xp/Stylesheet/expansion.text:
.xp-expansion {
    line-height: 1.2em;
    border-collapse: collapse;
    border-spacing: 0;
    padding: 9px 0;
    height: auto;
    width: 100%;
    text-align: left;
    text-transform: capitalize;
    border-top: 1px solid #ececec;
<$reveal state="$:/palette"type="nomatch"text="$:/palettes/Vanilla">
    border-top: 1px solid <<colour sidebar-tab-divider>>;
</$reveal>
}

.xp-expansion-text {
    padding: 0 0 9px;
    vertical-align: middle;
    max-height: 60vh;
    overflow: auto;
    -webkit-overflow-scrolling: touch;
}

.xp-expansion-chevron {
    float: right;
}

.xp-expansion-chevron svg {
    width: 0.7em;
    height: 0.7em;
    vertical-align: middle;
    color: <<colour sidebar-foreground>>;
    fill: <<colour sidebar-foreground>>;
}

.xp-chevron-up svg {
    -webkit-transform: rotate(-90deg);
    -o-transform: rotate(-90deg);
    -moz-transform: rotate(-90deg);
    -ms-transform: rotate(-90deg);
    transform: rotate(-90deg)
}

.xp-chevron-down svg {
    -webkit-transform: rotate(90deg);
    -o-transform: rotate(90deg);
    -moz-transform: rotate(90deg);
    -ms-transform: rotate(90deg);
    transform: rotate(90deg)
}

.xp-expansion-line {
    border-top: 1px solid #ececec;
<$reveal state="$:/palette"type="nomatch"text="$:/palettes/Vanilla">
    border-top: 1px solid <<colour sidebar-tab-divider>>;
</$reveal>
}

.xp-expansion-prompt {
    color: #00809d;
    font-size: 11px;
    line-height: 1.2;
    padding: 9px 0;
    text-transform: uppercase;
    letter-spacing: .02em;
}

$:/_xp/Stylesheet/formatting-list-result.text:
\define sidebarbreakpoint()
<$text text={{$:/themes/tiddlywiki/vanilla/metrics/sidebarbreakpoint}}/>
\end

<!-- Usage:
@@.twocolumns
<<list-links filter:"[tag[TiddlyWiki]]">>
@@
-->

<pre>/**  SHOW ON PC  **/

@media (min-width: <<sidebarbreakpoint>>) {
  /* TWO COLUMN MODE */
  .twocolumns {
    display: block;
    column-count: 2;
    column-gap: 1em;
    -moz-column-count: 2;
    -moz-column-gap: 1em;
    -webkit-column-count: 2;
    -webkit-column-gap: 1em;
  }

  /* THREE COLUMN MODE */
  .fourcolumns {
    display: block;
    column-count: 3;
    column-gap: 1em;
    -moz-column-count: 3;
    -moz-column-gap: 1em;
    -webkit-column-count: 3;
    -webkit-column-gap: 1em;
  }

  /* FOUR COLUMN MODE */
  .fourcolumns {
    display: block;
    column-count: 4;
    column-gap: 1em;
    -moz-column-count: 4;
    -moz-column-gap: 1em;
    -webkit-column-count: 4;
    -webkit-column-gap: 1em;
  }
}

