$:/plugins/souk21/commandpalette/Compact.text:
\rules only filteredtranscludeinline transcludeinline macrodef macrocallinline html
:root {
    --palette-radius: 5px;
}
.commandpalette {
    position: fixed;
    background: <<colour background>>;
    z-index: 9999999;
    left: 50%;
    top: 10%;
    transform: translate(-50%, 0%);
    width: 600px;
    display: none;
    flex-direction: column;
    font-size: 21px;
    border-radius: var(--palette-radius);
    box-shadow: rgba(0, 0, 0, 0.15) 0px 0px 14px;
    border: 2px solid <<colour page-background>>;
}
.inputhintwrapper {
    display: flex;
    align-items: center;
    border-bottom: 1px solid <<colour page-background>>;
}
.commandpalette input {
    border: 0;
    background: 0;
    height: 40px;
    color: <<colour foreground>>;
    border-radius: var(--palette-radius) var(--palette-radius) 0px 0px;
    outline: 0;
    min-width: 50px;
    flex: 1;
    padding-left: 7px;
}
.commandpalettehint {
    color:  <<colour foreground>>;
    font-size: 13px;
    text-align: right;
    flex-shrink: 0;
    padding-right: 7px;
    opacity: 0.6;
}
.commandpaletteresultselected>.commandpalettehint {
    color: <<colour background>>;
    opacity: 1;
}
.commandpalettehintmain {

}
.commandpaletteresult {
    background: <<colour background>>;
    font-size: 15px;
    color: <<colour foreground>>;
    display: flex;
    align-items: center;
    justify-content: space-between;
    padding-left: 6px;
    padding-top: 4px;
    padding-bottom: 4px;
    cursor: pointer;
}
.commandpaletteresult:not(:last-child) {
    border-bottom: <<colour page-background>> solid 1px;
}
.commandpaletteresult:hover:not(.commandpaletteresultselected) {
    background: <<colour page-background>>;
}
.commandpaletteresultselected {
    background: <<colour primary>>;
    color: <<colour page-background>>;
}
.cp-scroll{
    max-height: 50vh;
    overflow-y: auto;
    position: relative;
    border-radius: 0px 0px var(--palette-radius) var(--palette-radius);
}

$:/plugins/wikilabs/multicol-dropdown/st.text:
\define sidebarbreakpoint()
<$text text={{$:/themes/tiddlywiki/vanilla/metrics/sidebarbreakpoint}}/>
\end

/* concept used from: http://fourkitchens.com/blog/article/responsive-multi-column-lists-flexbox */

.tc-block-dropdown {
  display: flex;
  flex-wrap: wrap;
  padding: 3px;
  background: #eee;
}

.tc-dropdown-item {
  flex: 1 0 100%;
  margin-left: 2px;
  margin-top: 2px;
}

.tc-block-dropdown a{
  flex: 1 0 calc(33% - 2px);
  margin-left: 2px;
  margin-top: 2px;
}

.tc-block-dropdown .tc-tag-button-selected {
  background: <<colour primary>>;
}

.tc-search-results {
  width: 100%;
}

/* TODO this is needed, since otherwise the search results show an artefact */
/* This is already part of the reset.css .. but it has no effect .. bug? */

[hidden] {
  display: none;
}

@media (min-width: <<sidebarbreakpoint>>) {
  .tc-block-tags-dropdown {
    min-width: 900px !important;

    /* Compatible with Stories plugin */
    <$reveal default="yes" type="match" text={{{[{$:/_sq/Stories/Story2StoryList!!list}length[]match[0]then[no]] ~[{$:/config/_sq/Stories/story2}]}}}>

      <$reveal state="$:/state/leftopentab" type="match" text="yes" default="no">
        <$reveal state="$:/state/sidebar" type="match" text="yes" default="yes">
        min-width: 463px !important;
        margin-left: -45px !important;
        </$reveal>
        <$reveal state="$:/state/sidebar" type="match" text="no" default="yes">
        min-width: 470px !important;
        margin-left: -45px !important;
        </$reveal>
      </$reveal>

      <$reveal state="$:/state/leftopentab" type="match" text="no" default="no">
        <$reveal state="$:/state/sidebar" type="match" text="yes" default="yes">
        min-width: 430px !important;
        margin-left: -12px !important;
        </$reveal>
        <$reveal state="$:/state/sidebar" type="match" text="no" default="yes">
        min-width: 490px !important;
        margin-left: -12px !important;
        </$reveal>
      </$reveal>

    </$reveal>
  }
}

